sys_script_include.script:
var LifeSyncAISummary = Class.create();
LifeSyncAISummary.prototype = Object.extendsObject(AbstractAjaxProcessor, {
    generateSummary: function() {
        var sysId = this.getParameter('sysparm_sys_id');
        var document = this.getParameter('sysparm_document');

        var summary = "AI summary for " + document + ": This is a concise overview generated by AI.";

        var gr = new GlideRecord('x_1234_lifesync_lifesync_records');
        if (gr.get(sysId)) {
            gr.summary = summary;
            gr.update();
            return JSON.stringify({ success: true, summary: summary });
        }
        return JSON.stringify({ success: false, message: 'Record not found' });
    },

    type: 'LifeSyncAISummary'
});
